import React from 'react'
import { Icon, Menu } from 'semantic-ui-react'
import './Menu.css';
import { Button, Segment, Form } from 'semantic-ui-react'

import feed from "./resources/feed"

export default class PostMessageComponent extends React.Component {

  handleSubmit = (e) => {
       let data = JSON.parse(feed);  //parse the JSON
       data.employees.push({        //add the employee
           nickname:"Mike",
           type:"MSG",
           avatar:"",
           date: new Date(),
           message:""
       });
  }

  render() {

        return (
              <Segment inverted>
                <Form inverted>
                  <Form.Group widths='equal'>
                    <Form.Input fluid label='Nickname' placeholder='The one and only Ahmed' />
                    <Form.Input fluid label='Message' placeholder='Once upon a time..' />
                  </Form.Group>
                  <Form.Checkbox label='I agree to the Terms and Conditions' />
                  <Button type='submit' onClick={this.handleSubmitClick}>Submit</Button>
                </Form>
              </Segment>
        )
  }

}
